__global__ void add( int *a, int *b, int *c ) {    __syncthreads();        int tid = blockIdx.x;    for (;;) {        for (;;)            for (;;)                if (tid < N)                    c[tid] = a[tid] + b[tid];        if (tid < N) {            c[tid] = a[tid] + b[tid] + b[tid];        }    }    for (;;) {        for (;;) {            c[tid] = a[tid] + b[tid];        }    }    c[tid] = a[tid] + b[tid] + b[tid] + b[tid];    __shared__ int a = 1;    __shared__ int b = 1;    __syncthreads();}